//===================infinite loop post==================
function randomPost(json){
    console.log(json);

    // Generate the post
    var post = '';
    post += '<li>';
    post += '<article id="content">';
    post += '<a class="image"><img src="';
    post += json.image;
    post += '" alt="" />';
    post += '</a>';
    post += '<div class="content">';
    post += '<h3>';
    post += json.title;
    post += '</h3>';
    post += json.content.substring(0,50);
    post += '...';
    post += '<ul class="actions"><li><a onclick=passArticle("';
    post += json.title;
    post += '") class="button">閱讀</a></li></ul></div>';
    post += '</article>';
    post += '</li>';

    return post;

}

